// Use 'lighter' blend mode for realistic glow overlap
ctx.globalCompositeOperation = 'lighter'

// Layer 1: Bright core
ctx.shadowBlur = 15
ctx.shadowColor = {glowColor}
ctx.globalAlpha = {coreOpacity}
ctx.fillStyle = {glowColor}
ctx.beginPath()
ctx.arc({centerX}, {centerY}, {coreRadius}, 0, Math.PI * 2)
ctx.fill()

// Layer 2: Mid glow
ctx.shadowBlur = 35
ctx.globalAlpha = {midOpacity}
ctx.beginPath()
ctx.arc({centerX}, {centerY}, {midRadius}, 0, Math.PI * 2)
ctx.fill()

// Layer 3: Outer glow
ctx.shadowBlur = 65
ctx.globalAlpha = {outerOpacity}
ctx.beginPath()
ctx.arc({centerX}, {centerY}, {outerRadius}, 0, Math.PI * 2)
ctx.fill()

// Reset
ctx.globalCompositeOperation = 'source-over'
ctx.globalAlpha = 1.0
