const angleStep = (2 * Math.PI) / {boltCount}
for (let i = 0; i < {boltCount}; i++) {
  const angle = i * angleStep + {angleOffset}
  const startX = centerX + Math.cos(angle) * {centerRadius}
  const startY = centerY + Math.sin(angle) * {centerRadius}
  const endX = centerX + Math.cos(angle) * ({centerRadius} + {boltLength})
  const endY = centerY + Math.sin(angle) * ({centerRadius} + {boltLength})

  ctx.strokeStyle = boltColor
  ctx.lineWidth = {boltThickness}
  ctx.beginPath()
  ctx.moveTo(startX, startY)
  ctx.lineTo(endX, endY)
  ctx.stroke()
}
